#!/bin/bash
{{- range $index, $port := .Node.Ports}}
{{- $pam := portattributemap $port}}
{{- $gwa := get $pam "gateway" }}
{{- $gw := index $gwa 0 }}
ip addr add {{$port.Ipv4Address.String}}/{{$port.Ipv4Prefix.Int64}} dev {{$port.Name}}
{{if eq $index 0}}ip route add default via {{$gw.ValueString.String}}{{end}}
{{end}}

{{- $segments := multi .ModelStore "segments" "" -}}
{{- $nodes := multi .ModelStore "nodes" "preloads=Ports,NodeExtraAttributes,NodeExtraAttributes.NodeExtraAttributeField,NodeExtraAttributes.ValueNodeExtraAttributeOption" -}}
{{- $nodeMap := hash $nodes "ID" -}}
{{- $serverTypes := multi .ModelStore "node_extra_attribute_options" "q[node_extra_attribute_field_id]=3" }}
{{- $serverTypeMap := hash $serverTypes "ID" -}}
{{- $ports := multi .ModelStore "ports" "preloads=Node,PortExtraAttributes,PortExtraAttributes.PortExtraAttributeField,PortExtraAttributes.ValuePortExtraAttributeOption" -}}
{{- $portMap := hash $ports "ID" -}}
{{- $segmentActualPortMap := map -}}
{{- range $index, $segment := $segments -}}
  {{- $segmentName := printf "seg-seg%02d" $index -}}
  {{- $segmentActualPorts := slice -}}
  {{- $segmentActualPortMap := put $segmentActualPortMap $segmentName $segmentActualPorts -}}
{{- end -}}
{{- range $index, $segment := $segments -}}
  {{- $segmentName := printf "seg-seg%02d" $index -}}
  {{- range $index2, $port := $segment.Ports -}}
    {{- $port := get $portMap $port.ID -}}
    {{- $pam := portattributemap $port}}
    {{- $ps := get $pam "pass_through" }}
    {{- $passthrough := index $ps 0 }}
    {{- if eq $passthrough.ValueBool.Bool false}}
      {{- $segmentActualPorts := get $segmentActualPortMap $segmentName -}}
      {{- $segmentActualPorts := append $segmentActualPorts $port -}}
      {{- $segmentActualPortMap := put $segmentActualPortMap $segmentName $segmentActualPorts -}}
    {{- end -}}
  {{- end -}}
{{- end -}}
{{$segmentActualPortMap}}
provider "openvnet" {
  api_endpoint = "http://172.16.3.10:9090/api/1.0"
}

#
# MAC address range for use in datapath-network etc.
#
resource "openvnet_mac_range_group" "mrg" {
  uuid = "mrg-dpg"
  mac_range {
    begin_mac_address = "08:00:27:aa:00:00"
    end_mac_address   = "08:00:27:aa:ff:ff"
  }
}

#
# Physical network
#
resource "openvnet_network" "physical-network" {
  uuid         = "nw-phys1"
  display_name = "physical network that CI and ofs are on"
  ipv4_network = "172.16.4.0"
  ipv4_prefix  = 24
  network_mode = "physical"
}

#
# CI datapath
#
resource "openvnet_datapath" "dp-ci" {
  uuid         = "dp-ci"
  node_id      = "vna1"
  display_name = "ci_datapath"
  dpid         = "0x0000aaaaaaaaaaaa"
}

resource "openvnet_interface" "if-host-ci" {
  uuid                = "if-ci"
  mode                = "host"
  port_name           = "eth1"
  mac_address         = "02:01:00:00:00:01"
  owner_datapath_uuid = "${openvnet_datapath.dp-ci.id}"
  network_uuid        = "${openvnet_network.physical-network.uuid}"
  ipv4_address        = "172.16.4.10"
}

#
# OFS datapath
#
resource "openvnet_datapath" "dp-ofs" {
  uuid         = "dp-ofs"
  node_id      = "vna2"
  display_name = "ofs_datapath"
  dpid         = "0x0000bbbbbbbbbbbb"
}

# The IP lease on the ofs host interface is a fake. OFS and CI are on the
# same L2 network. This is done because the current tunnel manager code requires
# an IP lease but we can safely assign a fake one since MAC2MAC tunnels ignore
# IP addresses anyway. Only works if we're on the same L2 though.
resource "openvnet_interface" "if-host-ofs" {
  uuid                = "if-ofs"
  mode                = "host"
  port_name           = "eth9"
  mac_address         = "52:56:01:00:01:01"
  owner_datapath_uuid = "${openvnet_datapath.dp-ofs.uuid}"
  network_uuid        = "${openvnet_network.physical-network.uuid}"
  ipv4_address        = "172.16.4.11" # <= fake
}

resource "openvnet_topology" "topo-physical" {
  uuid = "topo-physical"
  mode = "simple_underlay"
  network {
    uuid = "${openvnet_network.physical-network.uuid}"
  }

  datapath {
    uuid = "${openvnet_datapath.dp-ci.uuid}"
    interface_uuid = "${openvnet_interface.if-host-ci.uuid}"
  }

  datapath {
    uuid = "${openvnet_datapath.dp-ofs.uuid}"
    interface_uuid = "${openvnet_interface.if-host-ofs.uuid}"
  }
}

#
# Below is the virtual network
# The segments which have one or more non pass-through mode interfaces will be generated
#
{{- range $index, $segment := $segments}}
  {{- $segmentName := printf "seg-seg%02d" $index }}
  {{- $segmentActualPorts := get $segmentActualPortMap $segmentName }}
  {{- $segmentActualPortCount := len $segmentActualPorts }}
  {{- if ne $segmentActualPortCount 0 }}
resource "openvnet_segment" "{{$segmentName}}" {
  uuid = "seg-seg{{printf "%02d" $index}}"
  mode = "virtual"
}
  {{- else}}
# The segment {{$segmentName}} has no non-pass through ports, skipped
  {{- end}}
{{- end}}

resource "openvnet_topology" "topo-vnet" {
  uuid = "topo-vnet"
  mode = "simple_overlay"

  underlay {
    uuid = "${openvnet_topology.topo-physical.uuid}"
  }

{{- range $index, $segment := $segments}}
  {{- $segmentName := printf "seg-seg%02d" $index }}
  {{- $segmentActualPorts := get $segmentActualPortMap $segmentName }}
  {{- $segmentActualPortCount := len $segmentActualPorts }}
  {{- if ne $segmentActualPortCount 0 }}
  segment {
    uuid = "${openvnet_segment.{{$segmentName}}.uuid}"
  }
  {{- else}}
  # The segment {{$segmentName}} has no non-pass through ports, skipped
  {{- end}}
{{- end}}
}

{{- $interfaceIndexMap := map}}
{{- range $index, $node := $nodes}}
  {{- $interfaceIndexMap := put $interfaceIndexMap $node.Name 0}}
{{- end}}
{{- range $index, $segment := $segments}}

#
# Interfaces in segment seg-seg{{printf "%02d" $index}}
# The interfaces which are not in pass-through mode will be generated
#
  {{- range $index2, $port := $segment.Ports}}
    {{- $port := get $portMap $port.ID}}
    {{- $node := get $nodeMap $port.Node.ID}}
    {{- $interfaceIndex := get $interfaceIndexMap $port.Node.Name}}
    {{- $pam := portattributemap $port}}
    {{- $ps := get $pam "pass_through" }}
    {{- $passthrough := index $ps 0 }}
    {{- if eq $passthrough.ValueBool.Bool false}}
      {{- $nam := nodeattributemap $node}}
      {{- $vs := get $nam "virtual" }}
      {{- $virtual := index $vs 0 }}
      {{- if eq $virtual.ValueBool.Bool false}}
resource "openvnet_interface" "{{$port.Node.Name}}-seg{{printf "%02d" $index}}" {
  uuid                = "if-{{$port.Node.Name}}{{$port.Name}}"
  port_name           = "{{$port.Name}}"
  owner_datapath_uuid = "${openvnet_datapath.dp-ofs.uuid}"
  segment_uuid        = "${openvnet_segment.seg-seg{{printf "%02d" $index}}.uuid}"
  mac_address         = "{{$port.MacAddress.String}}"
  mode                = "vif"
}
      {{- else}}
resource "openvnet_interface" "{{$port.Node.Name}}-{{printf "%02d" $interfaceIndex}}" {
  uuid                = "if-{{$port.Node.Name}}{{printf "%02d" $interfaceIndex}}"
  port_name           = "${openvdc_instance.{{$port.Node.Name}}.id}_{{printf "%02d" $interfaceIndex}}"
  segment_uuid        = "${openvnet_segment.seg-seg{{printf "%02d" $index}}.uuid}"
  mac_address         = "{{$port.MacAddress.String}}"
  mode                = "vif"
}
      {{- end}}
      {{- $interfaceIndex := add $interfaceIndex 1}}
      {{- $interfaceIndexMap := put $interfaceIndexMap $port.Node.Name $interfaceIndex}}
    {{- else}}
# The interface if-{{$port.Node.Name}}-{{$port.Name}} is in pass-through mode, skipped
    {{- end}}
  {{- end}}
{{- end}}

#
# OpenVDC instances
# Nodes which have a valid ServerType will be generated
#
{{- range $index, $node := $nodes }}
  {{- $nam := nodeattributemap $node }}
  {{- $sts := get $nam "server_type" }}
  {{- $st := index $sts 0 }}
  {{- if eq $st.ValueNodeExtraAttributeOptionID.Valid true }}
    {{- $so := $st.ValueNodeExtraAttributeOptionID.Int64 }}
    {{- $si :=  int $so }}
    {{- $serverType := get $serverTypeMap $si }}
resource "openvdc_instance" "{{$node.Name}}" {
  template = "{{$serverType.ValueString.String}}"

    {{- range $index2, $port := $node.Ports}}
  interface {
    type = "veth"
    bridge = "ovs"
    ipv4addr = "{{$port.Ipv4Address.String}}"
    macaddr = "{{$port.MacAddress.String}}"
  }

    {{- end}}
}
  {{- else}}
# The server type of the node {{$node.Name}} is None, skipped
  {{- end}}
{{- end}}

#
# Output
# Nodes which have a valid ServerType will be generated
#
{{- range $index, $node := $nodes }}
  {{- $nam := nodeattributemap $node }}
  {{- $sts := get $nam "server_type" }}
  {{- $st := index $sts 0 }}
  {{- if eq $st.ValueNodeExtraAttributeOptionID.Valid true }}
output "{{$node.Name}}" {
  value = "${openvdc_instance.{{$node.Name}}.id}"
}
  {{- else}}
# The server type of the node {{$node.Name}} is None, skipped
  {{- end}}
{{- end}}

#!/bin/bash

set -e

function loadInstanceIDs() {
  echo "declare -A instances"
  terraform show | egrep -o 'openvdc_instance.[^:]+:' | sed -e 's/openvdc_instance.\([^:]*\):/\1/g' | while read instance_name; do
    instance_id=`terraform show | grep -a1 "openvdc_instance.${instance_name}:" | tail -n1 | awk -F"= " '{print $2}'`
    echo "instances[${instance_name}]=${instance_id}"
  done
}

`loadInstanceIDs`

client_instance_id="${instances[{{.SourcePort.Node.Name}}]}"
server_instance_id="${instances[{{.DestinationPort.Node.Name}}]}"

openvdc wait ${client_instance_id} running
openvdc wait ${server_instance_id} running

openvdc console ${client_instance_id} <<EOF
ping -W 5 -c 1 {{.DestinationPort.Ipv4Address.String}}
ping -W 5 -c 1 {{.DestinationPort.Ipv4Address.String}}
ping -W 5 -c 1 {{.DestinationPort.Ipv4Address.String}}
ping -W 5 -c 4 {{.DestinationPort.Ipv4Address.String}}
{{if eq .Access true}}
if [ $? -eq 0 ]; then
  exit 0
else
  exit 1
fi
{{else}}
if [ $? -eq 0 ]; then
  exit 1
else
  exit 0
fi
{{end}}
EOF
