{{- $gwa := get $pam "gateway" }}
{{- $gw := index $gwa 0 }}
ip addr add {{$port.Ipv4Address.String}}/{{$port.Ipv4Prefix.Int64}} dev {{$port.Name}}
{{if eq $index 0}}ip route add default via {{$gw.ValueString.String}}{{end}}
{{end}}

